{
    "users": {
        "id": true,
        "email": true,
        "first_name": true,
        "last_name": true,
        "users_photo_id_fk": true,
        "phone": true,
        "photos": {
            "url": true
        },
        "$where": {
            "$and": [
                {
                    "$or": [
                        { "$like": ["last_name", { "$escape": "%%" }] },
                        { "$like": ["first_name", { "$escape": "%%" }] }
                    ]
                },
                { "$not": { "$eq": ["email", { "$escape": "mendeljacks@gmail.com" }] } }
            ]
        },
        "$offset": 0,
        "$limit": 10
    },
    "user_count": {
        "$from": "users",
        "count": { "$count": "*" },
        "$where": {
            "$and": [
                {
                    "$or": [
                        { "$like": ["last_name", { "$escape": "%%" }] },
                        { "$like": ["first_name", { "$escape": "%%" }] }
                    ]
                },
                { "$not": { "$eq": ["email", { "$escape": "mendeljacks@gmail.com" }] } }
            ]
        }
    }
}

{
      "places": {
        "id": true,
        "google_place_id": true,
        "$order_by": [
          {"$desc": "created_at"}
        ],
        "$offset": 0,
        "$limit": 100,
        "listings": {
          "id": true,
          "user_id": true,
          "reviews": {
            "id": true, 
            "listing_id": true
          }
        }
      }
    }

{
    "reviews": {
        "$offset": 0,
        "$limit": 2,
        "$where": {
            "$eq": [
                "listing_id",
                {
                    "$escape": 74
                }
            ]
        },
        "id": true,
        "listing_id": true,
        "created_at": true,
        "rating": true,
        "comment": true,
        "review_has_types": {
            "id": true,
            "review_type_id": true,
            "review_types": {
                "id": true,
                "name": true
            }
        },
        "likes_count": {
            "$select": [
                {
                    "$as": [
                        {
                            "$select": [
                                {
                                    "$count": "*"
                                }
                            ],
                            "$from": "likes",
                            "$where": {
                                "$eq": [
                                    "review_id",
                                    {
                                        "$entity": "reviews",
                                        "$field": "id"
                                    }
                                ]
                            }
                        },
                        "likes_count"
                    ]
                }
            ]
        },
        "users": {
            "id": true,
            "email": true,
            "first_name": true,
            "last_name": true,
            "photos": {
                "id": true,
                "url": true
            }
        }
    }
}

